import AWSConfig from 'src/aws-exports';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
const REGION = "us-east-1";
const client = new AWS.LexRuntimeV2(AWSConfig);
const SESSION = "session" + Date.now();
export class AppComponent {
    constructor(http) {
        this.http = http;
        this.title = "Takealot FAQ Bot";
        this.response = "";
        this.getResponse = async (input) => {
            try {
                const params = {
                    botId: "PWHSSUJNJX",
                    botAliasId: "80OHYBZJBJ",
                    localeId: "en_ZA",
                    sessionId: SESSION,
                    text: input
                };
                try {
                    const resp = await client.recognizeText(params);
                    return resp.messages[0].content;
                }
                catch (error) {
                    console.log(error);
                }
            }
            catch (err) {
                console.log("Error", err);
            }
        };
    }
    createSession() {
        client.putSession({
            sessionId: SESSION,
            botId: "PWHSSUJNJX",
            botAliasId: "80OHYBZJBJ",
            localeId: "en_ZA",
            sessionState: {
                intent: {
                    name: "Hello"
                }
            }
        });
    }
    input() {
        console.log("Clicked");
        let input = document.getElementById("input").value;
        let response = this.getResponse(input);
    }
}
